\begin{document}
$$\eqalign{
& fMRI\,_~\,1 + Age + (1{\rm{|}}Cohort) + PSQI*Cohort \cr 
& \,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\, + \left( {PSQI{\rm{|}}Cohort} \right) + Visit + Visit*Cohort \cr 
& \,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\, + (Visit|Cohort) + Sex \cr} $$
\end{document}
